@keyframes spinner {
  to {
    transform: rotate(360deg);
  }
}
@-webkit-keyframes spinner {
  to {
    -webkit-transform: rotate(360deg);
  }
}
.loading-spinner-large {
  min-width: 31px;
  min-height: 31px;
  display: block;
  position: relative;
}
.loading-spinner-large:before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 31px;
  height: 31px;
  margin-top: -15.5px;
  margin-left: -15.5px;
  border-radius: 50%;
  border: 2px solid #465153;
  border-top-color: #0b2c32;
  animation: spinner 0.7s cubic-bezier(0.645, 0.045, 0.355, 1) infinite;
  -webkit-animation: spinner 0.7s cubic-bezier(0.645, 0.045, 0.355, 1) infinite;
}
.loading-spinner-large.inline-block {
  display: inline-block;
  min-height: 1em;
}
.loading-spinner-medium {
  min-width: 26px;
  min-height: 26px;
