  margin-top: -13px;
  margin-left: -13px;
  border-radius: 50%;
  border: 2px solid #465153;
  border-top-color: #0b2c32;
  animation: spinner 0.7s cubic-bezier(0.645, 0.045, 0.355, 1) infinite;
