@-webkit-keyframes animate {
  0% {
    -webkit-transform: translate(-50%);
  }
  100% {
    -webkit-transform: translate(0);
  }
}
progress {
  -webkit-appearance: none;
  height: 8px;
  border-radius: 8px;
  overflow: hidden;
  background-color: #b7cbcf;
  border: 1px solid #809093;
  position: relative;
}
progress:after {
  content: '';
  -webkit-animation: animate 5s alternate ease-in-out infinite;
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 200%;
  height: 6px;
  background-image: linear-gradient(-45deg, transparent 33%, rgba(121, 84, 222, 0.66) 33%, rgba(121, 84, 222, 0.66) 66%, transparent 66%);
  background-size: 24px 8px, 100% 100%, 100% 100%;
  border-radius: 8px;
}
progress::-webkit-progress-bar {
  background-color: transparent;
}
progress::-webkit-progress-value {
  border-radius: 8px;
  background-color: #7954de;
}
progress[value] {
  background-image: none;
  -webkit-animation: none;
}
progress[value]:after {
  display: none;
}
