css:
.terminal-plus {
  letter-spacing: normal;
}
.terminal-plus ul {
  padding: 0;
  margin: 0;
}
.terminal-plus .icon::before {
  margin: 0;
  padding: 0;
}
.terminal-plus .icon-x::before {
  content: '\f081' !important;
  line-height: normal;
}
.terminal-plus.terminal-view {
  display: -webkit-flex;
  flex-direction: column;
  align-items: center;
  flex-wrap: nowrap;
  overflow-y: visible;
}
.terminal-plus.terminal-view .panel-divider {
  display: inherit;
  order: 1;
  z-index: 10;
  -webkit-user-select: none;
  cursor: ns-resize;
  width: 100%;
  height: 8px;
  margin-top: -4px;
}
.terminal-plus.terminal-view .btn-toolbar {
  display: block;
  order: 2;
  width: 100%;
  padding: 0 2px 4px 2px;
}
.terminal-plus.terminal-view .btn-toolbar .left {
  float: left;
}
.terminal-plus.terminal-view .btn-toolbar .right {
  float: right;
}
.terminal-plus.terminal-view .btn-toolbar .btn {
  top: 0;
  height: auto;
  line-height: normal;
  padding: 0;
  margin: 0 5px;
}
.terminal-plus.terminal-view .xterm {
  display: block;
  order: 3;
  height: 100%;
  width: 100%;
}
.terminal-plus.terminal-view .xterm > .terminal {
  height: 100%;
  width: 100%;
  position: relative;
  margin: 0 5px;
}
.terminal-plus.terminal-view .xterm > .terminal > div {
  white-space: pre;
  font-size: inherit;
}
.terminal-plus.terminal-view .xterm > .terminal > div > span {
  display: inline-block;
  line-height: inherit;
  height: inherit;
}
.terminal-plus.status-bar {
  display: -webkit-flex;
  flex-direction: row;
  align-items: center;
  -webkit-user-select: none;
  margin: 0 10px;
  box-sizing: content-box;
  height: 18px;
}
.terminal-plus.status-bar i {
  display: inherit;
  text-align: center;
  cursor: pointer;
  height: 16px;
  width: 16px;
  line-height: 16px;
  align-self: center;
}
.terminal-plus.status-bar .icon-x {
  color: red;
  justify-content: flex-end;
}
.terminal-plus.status-bar .status-container {
  display: inherit;
  position: relative;
  width: 100%;
  height: 100%;
  margin: 0 5px;
}
.terminal-plus.status-bar .status-container .status-icon {
  display: inherit;
  -webkit-transition: opacity 0.25s ease-in-out, color 0.5s ease-in-out;
  -webkit-user-drag: element;
  padding: 0 5px;
  opacity: 0.5;
  height: 100%;
}
.terminal-plus.status-bar .status-container .status-icon.active {
  opacity: 1;
}
.terminal-plus.status-bar .status-container .is-dragging {
  border-bottom: 1px solid #2196f3;
}
.terminal-plus.status-bar .status-container .placeholder {
  pointer-events: none;
  position: absolute;
  top: -16px;
  color: #2196f3;
  z-index: 999;
  margin: 0 -6px;
  padding: 0;
  height: 10px;
  line-height: 10px;
}
.terminal-plus.status-bar .status-container .placeholder::before {
  font-size: 16px;
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  content: "\f05b";
}
.terminal-plus .inserted {
  -webkit-animation: flash 0.25s linear 6 alternate;
}
@-webkit-keyframes flash {
  0% {
    opacity: 0.5;
  }
  100% {
    opacity: 1;
  }
}
