.tooltip {
  white-space: nowrap;
}
.tooltip .keystroke {
  font-family: 'SF UI Text', 'Helvetica Neue', 'Helvetica', 'Arial', sans-serif;
  letter-spacing: 0.04em;
  color: #ffffff !important;
  font-weight: 300;
