atom-workspace .tree-view {
  color: #a9aeb4;
  font-family: 'SF UI Text', 'Helvetica Neue', 'Helvetica', 'Arial', sans-serif;
  letter-spacing: 0.04em;
  font-weight: 300;
  font-size: 13px;
  background: #2d3a40;
  padding: 0;
  box-sizing: border-box;
}
atom-workspace .tree-view > .directory > .header {
  transition: all 400ms ease;
  background: #2d3a40;
  height: 32px;
  line-height: 32px;
}
atom-workspace .tree-view > .directory > .header span {
  font-family: 'SF UI Text', 'Helvetica Neue', 'Helvetica', 'Arial', sans-serif;
  letter-spacing: 0.04em;
  font-weight: 600;
  color: #ffffff;
  transition: all 400ms ease;
}
atom-workspace .tree-view > .directory > .header span:before {
  display: none;
}
atom-workspace .tree-view > .directory > .header:hover span {
  color: #ffffff;
}
atom-workspace .tree-view > .directory > .header:before {
  visibility: hidden;
}
atom-workspace .tree-view > .directory > .header:after {
  content: "hide";
  font-family: 'SF UI Text', 'Helvetica Neue', 'Helvetica', 'Arial', sans-serif;
  letter-spacing: 0.04em;
  color: #6b747a;
  font-weight: 300;
  font-size: 10px;
  position: absolute;
  right: 10px;
  opacity: 0;
  text-transform: uppercase;
  transform: scale(0);
  transition: all 400ms ease;
}
atom-workspace .tree-view > .directory > .header:hover {
  background: #3c4d55;
}
atom-workspace .tree-view > .directory > .header:hover:after {
  opacity: 1.0;
  transform: scale(1);
}
atom-workspace .tree-view > .directory > .entries {
  opacity: 1;
  padding-bottom: 10px;
  transition: all 400ms ease;
}
atom-workspace .tree-view > .directory.collapsed > .header span {
  color: #6b747a;
}
atom-workspace .tree-view > .directory.collapsed > .header:hover span {
  color: #ffffff;
}
atom-workspace .tree-view > .directory.collapsed > .header:after {
  content: "show";
}
atom-workspace .tree-view > .directory.collapsed > .entries {
  display: block !important;
  padding-bottom: 0;
  opacity: 0;
}
