.settings-view .copy-icon {
  cursor: pointer;
  padding-right: 2px;
}
.settings-view .copy-icon:hover {
  color: #2a6496;
}
.settings-view .table table {
  width: 100%;
  table-layout: initial;
}
.settings-view .table th {
  padding: -0.5em 1.5em;
  padding-left: 0;
}
.settings-view .table td {
  height: 2em;
}
.settings-view .table td,
.settings-view .table th {
  padding-left: 1%;
  padding-right: 1%;
  white-space: nowrap;
  overflow: hidden;
  border-color: #f9f9fc;
}
.settings-view .table col.keystroke {
  width: 13%;
}
.settings-view .table col.command {
  width: 38%;
}
.settings-view .table col.source {
  width: 8%;
}
.settings-view .table col.selector {
  width: 33%;
}
.settings-view .compile-tools-heading {
  font-weight: bold;
}
.settings-view .compile-tools-message {
  padding-top: 1.5em;
}
.settings-view .compile-tools-message code {
  text-shadow: none;
  border-width: 1px;
  border-style: solid;
}
@media all and (max-width: 800px) {
  .thumbnail .btn-toolbar .btn {
    width: 100%;
    margin: 2px 0;
  }
}
.available-updates-status {
  padding-left: 2px;
}
.settings-view .container {
  padding: 0;
}
.settings-view .container > .row {
  margin-left: 0;
  margin-right: 0;
}
.settings-view .update-all-button {
  top: -4px;
  margin-left: 4px;
  right: 0;
  position: relative;
}
.settings-view .keybinding-panel .is-user {
  color: #454546;
  background-color: #71a7ca;
}
.settings-view .btn-wrap-group .btn {
  margin: 0 0.75em 0.75em 0;
}
.settings-view .package-readme {
  font-size: 1.25em;
}
.settings-view .package-readme h1 {
  font-size: 2em;
  margin-top: 1.5em;
}
.settings-view .package-readme h2 {
  font-size: 1.75em;
  margin-top: 1.5em;
}
.settings-view .package-readme h3 {
  font-size: 1.5em;
  margin-top: 1.75em;
}
.settings-view .package-readme h4 {
  font-size: 1.25em;
  margin-top: 1.25em;
}
.settings-view .package-readme h1,
.settings-view .package-readme h2,
.settings-view .package-readme h3,
.settings-view .package-readme h4 {
  color: #404041;
}
.settings-view .package-readme img {
  max-width: 100%;
}
.clearfix:before {
  display: table;
  content: "";
}
.clearfix:after {
  display: table;
  clear: both;
  content: "";
}
@media (min-width: 768px) {
  .container {
    width: auto;
  }
}
@media (min-width: 992px) {
  .container {
    width: auto;
  }
}
@media (min-width: 1200px) {
  .container {
    width: auto;
  }
  .col-lg-8 {
    width: 100%;
  }
}
