/*
 * ============
 * = SETTINGS =
 * ============
 */
.settings-view {
  /*
	 * ========
	 * = MENU =
	 * ========
	 */
  /*
	 * ===========
	 * = SECTION =
	 * ===========
	 */
  /*
	 * =============
	 * = CONTAINER =
	 * =============
	 */
  /*
	 * =========
	 * = TITLE =
	 * =========
	 */
  /*
	 * ========
	 * = CARD =
	 * ========
	 */
  /*
	 * =============
	 * = COMPONENT =
	 * =============
	 */
}
.settings-view .config-menu {
  position: relative;
  min-width: 15em;
  max-width: 20em;
  border-width: 0 1px 0 0;
