  width: 0.375em;
}
.settings-view .package-card .meta-controls .status-indicator:before {
  content: "\00a0";
}
.settings-view .icon::before {
  color: rgba(69, 69, 70, 0.6);
}
.settings-view .editor-container {
  margin: 1.5em 0;
}
.settings-view .form-control {
  font-size: 1.25em;
  height: 2.5em;
}
