.results-view .path-details.list-item {
  color: #121213;
}
.tooltip {
  white-space: nowrap;
  font-size: 1.15em;
}
.tooltip.in {
  opacity: 1;
  transition: opacity 0.12s ease-out;
}
.tooltip .tooltip-inner {
  line-height: 1;
  padding: 0.75em 0.975em;
  border-radius: 2px;
  background-color: #71a7ca;
  color: #f2f2f7;
  white-space: nowrap;
  max-width: none;
}
.tooltip .keystroke {
  font-family: "Helvetica Neue", Arial, sans-serif;
  font-size: 1em;
  padding: .1em .4em;
  margin: 0 -0.525em 0 0.375em;
  border-radius: 2px;
  color: #f2f2f7;
  background: #71a7ca;
}
.tooltip.top .tooltip-arrow {
  border-top-color: #71a7ca;
}
.tooltip.top-left .tooltip-arrow {
  border-top-color: #71a7ca;
}
.tooltip.top-right .tooltip-arrow {
  border-top-color: #71a7ca;
}
.tooltip.right .tooltip-arrow {
  border-right-color: #71a7ca;
}
.tooltip.left .tooltip-arrow {
  border-left-color: #71a7ca;
}
.tooltip.bottom .tooltip-arrow {
  border-bottom-color: #71a7ca;
}
.tooltip.bottom-left .tooltip-arrow {
  border-bottom-color: #71a7ca;
}
.tooltip.bottom-right .tooltip-arrow {
  border-bottom-color: #71a7ca;
}
.tree-view {
  font-size: 1em;
  background: #f2f2f7;
  /*
   * ================
   * = PROJECT ROOT =
   * ================
   */
}
.tree-view .project-root.project-root {
  padding-top: 0.75em;
}
.tree-view .project-root.project-root:before {
  height: 3em;
  border-top: 1px solid transparent;
  background-clip: padding-box;
}
.tree-view .project-root.project-root > .header .name {
  line-height: 3em;
  font-size: 1.1em;
  color: #71a7ca;
}
.tree-view .project-root.project-root > .header:hover .name:hover {
  color: #5e9bc3;
}
.tree-view .project-root.project-root:focus {
  background: #ffffff;
}
.tree-view .selected:before {
  background: #ffffff !important;
}
.tree-view:focus .selected:before {
  background: #71a7ca;
}
.tree-view-resizer .tree-view-resize-handle {
  width: 8px;
}
/*
 * =====================
 * = EMS-BASED HEIGHTS =
 * =====================
 */
.list-group li:not(.list-nested-item),
.list-tree li:not(.list-nested-item),
.list-group li.list-nested-item > .list-item,
.list-tree li.list-nested-item > .list-item {
  line-height: 2.25em;
}
.list-group .selected::before,
.list-tree .selected::before {
  height: 2.25em;
}
/*
 * ========
 * = ICON =
 * ========
 */
.list-group .icon,
.list-tree .icon {
  display: inline-block;
  height: inherit;
}
.list-group .icon::before,
.list-tree .icon::before {
  top: initial;
  line-height: inherit;
  height: inherit;
  vertical-align: top;
}
.status-bar {
  font-size: 1em;
  height: 2.625em;
  line-height: 2.625em;
}
.status-bar .flexbox-repaint-hack {
  padding: 0 0.75em;
}
.status-bar .inline-block {
  margin-right: 1.5em;
  vertical-align: top;
}
.status-bar .pull-right .inline-block {
  margin-right: 0;
  margin-left: 1.5em;
}
.status-bar .pull-right .inline-block > .inline-block {
  margin-left: 0;
}
.status-bar .pull-right .icon {
  margin-right: 0.375em;
}
.status-bar .icon::before {
  font-size: 16px;
  width: 16px;
  height: 16px;
}
.key-binding {
  display: inline-block;
  margin-left: 0.45454545em;
  padding: 0 0.375em;
  line-height: 2;
  font-family: Helvetica Neue, Arial, sans-serif;
  font-size: 1em;
  letter-spacing: 0.1em;
  border-radius: 2px;
  border: 1px solid #71a7ca;
  background-color: #ffffff;
}
