}
.panel {
  border-radius: 0;
  border: none;
  background-color: transparent;
  box-shadow: none;
}
.panel.bordered .panel-heading {
  border-radius: 2px 2px 0 0;
}
.inset-panel .panel-heading {
  border-radius: 2px 2px 0 0;
}
.panel-heading {
  margin: 0;
  padding: 1.5em;
  border-radius: 0;
  font-size: 1em;
  line-height: 1;
}
.panel-heading .btn {
  height: -3em;
  line-height: -3em;
  font-size: -1em;
