atom-panel.right {
  background-color: #f2f2f7;
}
.tool-panel {
  position: relative;
  background-color: #f2f2f7;
}
.overlay,
atom-panel.modal {
  position: absolute;
  left: 50%;
  width: 500px;
  margin-left: -250px;
  z-index: 9999;
  box-sizing: border-box;
  color: #454546;
  background-color: #f2f2f7;
  padding: 10px;
}
.overlay h1,
atom-panel.modal h1 {
  margin-top: 0;
